pragma solidity ^0.4.18;


contract Shipper_Importer {
    struct Shipment {
        uint shipment_id;
        address exporter_id;
        address shipper_id;
        string name;
        string description;
        uint load_weight;
        uint load_value;
        uint transport_charges; //depend on load_weight    
    }

    mapping(uint => Shipment) public shipments;
    uint shipment_counter;

    event LogShipment(
        uint indexed shipment_id,
        address indexed exporter_id,
        address indexed shipper_id,
        string name,
        string description,
        uint load_weight,
        uint load_value,
        uint transport_charges

    );

    function loadShipment(
        string _name,
        string _description,
        uint _load_weight,
        uint _load_value,
        uint _transport_charges

    ) public {
        shipment_counter++;

        shipments[shipment_counter] = Shipment(
            shipment_counter,
            msg.sender,
            0x0,
            _name,
            _description,
            _load_weight,
            _load_value,
            _transport_charges

        );

        emit LogShipment(
            shipment_counter,
            msg.sender,
            0x0,
            _name,
            _description,
            _load_weight,
            _load_value,
            _transport_charges
            );
    }

    function getNumberOfShipments() public view returns (uint) {
        return shipment_counter;
    }

}
